![效果](/img/c-5.jpg)
还是看代码吧
``` C
#include <string.h>
#include <stdio.h>
#include <stdlib.h> 
int main(void)
{
    ///方法定义
    char* join(char  *s1,char *s2);
    int indexOf(char *str1,char *str2);
    int lastIndexOf(char *str1,char *str2);
    char* ltrim(char *str);
    char* rtrim(char *str);
    char* trim(char *str);
    //测试数据准备
    char *first;
    char *second;
    char *indexstr;
    char *third;
    char *show;
    first = "binyanbin website is ";
    second = "http://binyanbin.github.io";
    indexstr = "bin";
    third = "  binyanbin trim test ";
    //测试结果输出
    show = join(first,second);
    printf("join(\"%s\",\"%s\")=\"%s\"\n",first,second,show);
    free(show);
    printf("indexOf(\"%s\",\"%s\")=%d\n",second,indexstr,indexOf(second,indexstr));
    printf("lastIndexOf(\"%s\",\"%s\")=%d\n",second,indexstr,lastIndexOf(second,indexstr));
    show = ltrim(third);
    printf("ltrim(\"%s\")=\"%s\"\n",third,show);
    free(show);
    show = rtrim(third);
    printf("rtrim(\"%s\")=\"%s\"\n",third,show);
    show = trim(third);
    printf("trim(\"%s\")=\"%s\"",third,show);
    free(show);
}

//字符串连接
char* join(char  *s1,char *s2)
{
    //分布内存
    char *result = malloc(strlen(s1)+strlen(s2)+1);
    if (result==NULL) 
        exit(0);//内存为空退出
    //拷贝s1到result
    strcpy(result, s1);
    //s2加到result尾部  
    strcat(result, s2);  
    return result;  
}
//字符串查询最开始位置
int indexOf(char *str1,char *str2)  
{  
    char *p=str1;  
    int i=0;  
    //p是一个地址，找到相同字符串的地址
    p=strstr(str1,str2);  
    if(p==NULL)  
        return -1;  
    else{  
        //是不是同一个地址了，是同一地址则找到字符串
        while(str1!=p)
        {  
            str1++;  
            i++;  
        }  
    }  
    return i;  
} 

//查询最后位置
int lastIndexOf(char *str1,char *str2)  
{  
    char *p=str1;  
    int i=0;
    int len=strlen(str2);  
    p=strstr(str1,str2);  
    if(p==NULL)return -1;
    //未找到字符串地址为空则跳出循环  
    while(p!=NULL)  
    { 
        //跳过已找的地址 
        for(;str1!=p;str1++)
            i++;  
        p=p+len;
        //找出下一个同样字符串的地址  
        p=strstr(p,str2);  
    }  
    return i;  
} 

//删除左空格 
char* ltrim(char *param)  
{
    //申请存储，不修改参数内存的数据
    char *str = malloc(strlen(param)+1); 
    strcpy(str, param); 
    int i=0,j,len=strlen(str);  
    while(str[i]!='\0')  
    {
        //32:空格,9:横向制表符
        if(str[i]!=32&&str[i]!=9)
            break;
        i++;  
    }  
    if(i!=0)  
    //将后面的字符顺势前移,补充删掉的空白位置
    for(j=0;j<=len-i;j++)  
    {     
        str[j]=str[j+i];
    }  
    return str; 
}  
//删除右空格
char* rtrim(char *param)  
{
    char *str = malloc(strlen(param)+1);    
    strcpy(str, param); 
    char *p=str;  
    int i=strlen(str)-1;  
    while(i>=0)  
    {  
        if(p[i]!=32&&p[i]!=9)
            break;  
        i--;  
    }  
    str[++i]='\0';
    return str;  
}
//删除左右空格
char* trim(char *str)
{
    char* temp = ltrim(str);
    char* result = rtrim(temp);
    free(temp);
    return result;
}  
```

